# unpacking stuff with tar is obnoxious
# heres a useful shell script for unpacking with tar
# throw it in your .bashrc as a function or whatever.
if [ -f "$1" ] ; then
	case "$1" in
		*.tar.bz2)   tar xvjf "$1"    ;;
		*.tar.gz)    tar xvzf "$1"    ;;
		*.tar)       tar xvf "$1"     ;;
		*.tbz2)      tar xvjf "$1"    ;;
		*.tgz)       tar xvzf "$1"    ;;
		*.tar.xz)    tar xJf "$1"     ;;
		*) echo "don't know how to extract "$1"..." ;;
	esac
else
	echo ""$1" is not a valid file!"
fi
